        overflowLength=elmainLengh-elmaskLengh;
        if(sliderSize>-overflowLength){
            $E('#slidergoods .GoodsListWrap').tween('left', sliderSize);
        }
        else{
            $E('#slidergoods .GoodsListWrap').tween('left', 0); 
            sliderSize=0;   
        }
