<?php if(!function_exists('tpl_input_default')){ require(CORE_DIR.'/include_v5/smartyplugins/input.default.php'); } if(!function_exists('tpl_modifier_storager')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.storager.php'); }  if( $this->_vars['pic_bar'] ){  echo $this->_vars['pic_bar'];  }else{  if( $this->_vars['goods']['udfimg'] ){  echo tpl_input_default(array('id' => "udfimg",'name' => "goods[udfimg]",'type' => "hidden",'value' => $this->_vars['goods']['udfimg']), $this); }else{  echo tpl_input_default(array('id' => "udfimg",'name' => "goods[udfimg]",'type' => "hidden",'value' => "false"), $this); }  echo tpl_input_default(array('name' => "goods[db_thumbnail_pic]",'type' => "hidden",'value' => $this->_vars['goods']['thumbnail_pic']), $this); echo tpl_input_default(array('name' => "goods[db_small_pic]",'type' => "hidden",'value' => $this->_vars['goods']['small_pic']), $this); echo tpl_input_default(array('name' => "goods[db_big_pic]",'type' => "hidden",'value' => $this->_vars['goods']['big_pic']), $this);?> <div id="imageuparea" style="display:<?php if( $this->_vars['goods']['udfimg']=='true' ){ ?>block<?php }else{ ?>none<?php } ?>" class="notice"> 注：<strong>列表页商品图片</strong>默认由系统自动生成.您也可以上传一张商品图片来覆盖默认的.<br /> <strong>单独上传：</strong> <?php echo tpl_input_default(array('name' => "goods_thumbnail_pic",'type' => "file",'size' => '9','onmouseover' => "this.title=this.value"), $this);?><br/><hr/> 当前缩略图浏览：<?php echo tpl_input_default(array('type' => 'viewIMG','value' => tpl_modifier_storager(((isset($this->_vars['goods']['thumbnail_pic']) && ''!==$this->_vars['goods']['thumbnail_pic'])?$this->_vars['goods']['thumbnail_pic']:$this->system->getConf('site.default_thumbnail_pic')))), $this);?> </div> <div id="x-main-pic" class='textcenter'> <?php if( $this->_vars['goods']['image_default'] ){ ?> <script>
              (function(){
                try{
                <?php if( $this->_vars['goods']['small_pic'] ){ ?>
                  goodsEditor.pic.setDefault('<?php echo $this->_vars['goods']['image_default']; ?>','<?php echo tpl_modifier_storager($this->_vars['goods']['small_pic']); ?>');
