                })();
            </script> <?php }else{ ?> <p align="center"><br /><br />此处显示商品页默认图片<br />[您还未上传商品图片！]</p> <?php } ?> </div> <div id='all-pics'> <?php if( $this->_vars['goods']['gimages'] ){  foreach ((array)$this->_vars['goods']['gimages'] as $this->_vars['gimage']){ ?> <div class="gpic-box"><?php $_tpl_tpl_vars = $this->_vars; echo $this->_fetch_compile_include("product/gimage.html", array()); $this->_vars = $_tpl_tpl_vars; unset($_tpl_tpl_vars); ?></div> <?php }  } ?> </div> <?php } ?>