<?php if(!function_exists('tpl_modifier_number')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.number.php'); } if(!function_exists('tpl_function_link')){ require(CORE_DIR.'/include_v5/smartyplugins/function.link.php'); } if(!function_exists('tpl_function_pager')){ require(CORE_DIR.'/include_v5/smartyplugins/function.pager.php'); } $CURRENCY = &$this->system->loadModel('system/cur'); ?><div class="ItemsWarp clearfix"> <div class="GoodsInfoWrap PackageGoodsWrap"> <?php foreach ((array)$this->_vars['package'] as $this->_vars['itemlist']){ ?> <div class="items clearfix"> <h3><?php echo $this->_vars['itemlist']['name']; ?></h3> <?php if( $this->_vars['itemlist']['intro'] ){ ?> <div class='desc'><?php echo $this->_vars['itemlist']['intro']; ?></div> <?php } ?> <div class="span-6"> <div class="boxBlue"> <div class="division"> <ul class="clearfix"> <li ><div class="span-2">原价：</div><strong class="mktprice1"><?php echo $CURRENCY->changer($this->_vars['itemlist']['mktprice']); ?></strong></li> <li ><div class="span-2">捆绑价：</div><strong class="price1"><?php echo $CURRENCY->changer($this->_vars['itemlist']['price']); ?></strong></li> <?php if( $this->_vars['itemlist']['mktprice'] > $this->_vars['itemlist']['price'] && $this->_vars['setting']['mktprice'] && $this->_vars['setting']['saveprice'] > 0 ){ ?> <li class="save"><div class="span-2"></div> <?php if( $this->_vars['setting']['saveprice'] == 1 ){ ?>节省：<strong><?php echo $CURRENCY->changer($this->_vars['itemlist']['mktprice']-$this->_vars['itemlist']['price']); ?></strong> <?php }elseif( $this->_vars['setting']['saveprice'] == 2 ){ ?>优惠：<strong><?php echo tpl_modifier_number($this->_vars['itemlist']['price']/$this->_vars['itemlist']['mktprice']*100,'2'); ?>% <?php }elseif( $this->_vars['setting']['saveprice'] == 3 ){ ?><strong><?php echo tpl_modifier_number($this->_vars['itemlist']['price']/$this->_vars['itemlist']['mktprice']*10,'1'); ?>折 <?php } ?></strong></li> <?php } ?> <li class="textcenter"><a class="actbtn btn-buy" href="<?php echo tpl_function_link(array('ctl' => "cart",'act' => "addPkgToCart",'arg0' => $this->_vars['itemlist']['goods_id']), $this);?>">购买</a></li> </ul> </div> </div> </div> <div class="push-6"> <h4>此捆绑促销包括：</h4> <ul class="list"> <?php foreach ((array)$this->_vars['itemlist']['items'] as $this->_vars['items']){ ?> <li><a href="<?php echo tpl_function_link(array('ctl' => "product",'act' => "index",'arg0' => $this->_vars['items']['p_goods_id']), $this);?>" target="_blank"><?php echo $this->_vars['items']['name']; ?></a><br /> 数量:<strong class="fontcolorBlue"><?php echo $this->_vars['items']['pkgnum']; ?></strong> 单价:<strong class="fontcolorBlue"><?php echo $CURRENCY->changer($this->_vars['items']['price']); ?></strong></li> <?php } ?> </ul> </div> <!-- <ul class="button"><li title="<?php echo $this->_vars['itemlist']['name']; ?>" star="<?php echo $this->_vars['itemlist']['goods_id']; ?>" class="star-off"><a class="listact" onclick="return false;" href="#">加入收藏</a></li><li class="vdetail"></li><li class="addcart"></li></ul>--> </div> <?php } ?> </div> <?php echo tpl_function_pager(array('data' => $this->_vars['pager']), $this);?> </div> 