<?php if(!function_exists('tpl_modifier_amount')){ require(CORE_DIR.'/include_v5/smartyplugins/modifier.amount.php'); } $CURRENCY = &$this->system->loadModel('system/cur'); ?> <div class='clearfix'> <table width="100%" cellpadding="0" cellspacing="0" class="liststyle data"> <col class="span-auto ColColorGray"></col> <col class="span-4 ColColorOrange textright"></col> <tr> <th>商品总价格：</th> <td value="<?php echo $this->_vars['trading']['pmt_b']['totalPrice']; ?>" class="price"><?php echo $CURRENCY->changer($this->_vars['trading']['pmt_b']['totalPrice']); ?></td> </tr> <?php if( $this->_vars['trading']['shipping_id'] ){ ?> <tr> <th>配送费用：</th> <td><?php echo $CURRENCY->changer($this->_vars['trading']['cost_freight']); ?></td> </tr> <?php }  if( $this->_vars['trading']['is_protect'] ){ ?> <tr> <th>配送保价费：</th> <td><?php echo $CURRENCY->changer($this->_vars['trading']['cost_protect']); ?></td> </tr> <?php }  if( $this->_vars['trading']['cost_payment'] ){ ?> <tr> <th>支付费率：(客户承担支付手续费)</th> <td><?php echo $CURRENCY->changer($this->_vars['trading']['cost_payment']); ?></td> </tr> <?php }  if( count($this->_vars['trading']['pmt_o']['list'])>0 or count($this->_vars['trading']['gift_p'])>0 or count($this->_vars['trading']['coupon_p'])>0 ){  if( count($this->_vars['trading']['pmt_o']['list'])>0 ){ ?> <tr> <th>享受的优惠：</th> <td> <ol> <?php foreach ((array)$this->_vars['trading']['pmt_o']['list'] as $this->_vars['item']){ ?> <li><?php echo $this->_vars['item']['pmt_describe']; ?></li> <?php } ?> </ol> </td> </tr> <?php }  if( count($this->_vars['trading']['gift_p'])>0 ){ ?> <tr> <th>获得赠品：</th> <td> <ol> <?php foreach ((array)$this->_vars['trading']['gift_p'] as $this->_vars['key'] => $this->_vars['item']){ ?> <li <?php if( $this->_vars['item']['storage']==0 ){ ?>class="mktprice1"<?php } ?>><?php echo $this->_vars['item']['name'];  echo $this->_vars['item']['nums']; ?> 件&nbsp;&nbsp;<?php if( $this->_vars['item']['storage']==0 ){ ?>（无货）<?php } ?></li> <?php } ?> </ol> </td> </tr> <?php }  if( count($this->_vars['trading']['coupon_p'])>0 ){ ?> <tr> <th>获得优惠券：</th> <td> <ol> <?php foreach ((array)$this->_vars['trading']['coupon_p'] as $this->_vars['key'] => $this->_vars['item']){ ?> <li><?php echo $this->_vars['item']['cpns_name'];  echo $this->_vars['item']['nums']; ?> 张</li> <?php } ?> </ol></td> </tr> <?php }  }  if( $this->_vars['trading']['pmt_amount'] ){ ?> <tr> <th>优惠金额：</th> <td style="color:#f00;">-<?php echo $CURRENCY->changer($this->_vars['trading']['pmt_amount']); ?></td> </tr> <?php }  if( $this->_vars['trading']['discount'] != 0 ){ ?> <tr> <th>订单减免：</th> <td style="color:#f00;"><?php if( $this->_vars['trading']['discount']>0 ){ ?>-<?php echo $CURRENCY->changer($this->_vars['trading']['discount']);  }else{ ?>+<?php echo $CURRENCY->changer(0-$this->_vars['trading']['discount']);  } ?></td> </tr> <?php }  if( $this->_vars['trading']['totalConsumeScore'] ){ ?> <tr> <th>抵扣积分：</th> <td style="color:#f00;"><?php echo $this->_vars['trading']['totalConsumeScore']; ?></td> </tr> <?php }  if( $this->_vars['trading']['cur_code'] != '' && $this->_vars['trading']['cur_code'] != $this->_vars['trading']['cur_display'] ){ ?> <tr> <th>货币汇率:</th> <td><?php echo $this->_vars['trading']['cur_rate']; ?></td> </tr> <tr> <th>货币结算金额:</th> <td><?php echo tpl_modifier_amount($this->_vars['trading']['final_amount'],$this->_vars['trading']['cur_code'],false,false,true); ?></td> </tr> <?php }  if( $this->_vars['trading']['cost_tax']>0 ){ ?> <tr> <th>开发票所需税金：</th> <td><?php echo $CURRENCY->changer($this->_vars['trading']['cost_tax']); ?></td> </tr> <?php } ?> <tr> <th>订单总金额：</th> <td><span class="price1"><?php echo tpl_modifier_amount($this->_vars['trading']['total_amount']); ?></span> <br> <?php if( $this->_vars['trading']['is_tax'] == '1' ){ ?> 是否需要发票?(税金:<strong><?php echo $this->_vars['trading']['tax_rate']*100; ?>%</strong>)<input type="checkbox" id="is_tax" name="payment[is_tax]" onclick="Order.updateTotal()" <?php if( $this->_vars['trading']['tax_checked'] ){ ?>checked=checked<?php } ?> value="true"> <p id='tax_company'> 发票抬头：<input type="text" name="payment[tax_company]" class="inputstyle" ></p> <script>
